import React, { Component } from 'react';
import Home from './Home';
import AddEvent from './AddEvent';
import './App.css';

class App extends Component {           //Here, there is no React.component as it has been imported above
  state = {
    users : [
      {name:'Chirag', age:22, belt:'A', id:1},
      {name:'Parag', age:32, belt:'B', id:2},
      {name:'Patel', age:52, belt:'A', id:3}
    ]
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1>This is a React Project</h1>
          <p>This is an introduction</p>
          <Home users={this.state.users} />         {/* This is to tell it what component and what 'prop'(erty) to render in the webpage */}
          <div>
            <AddEvent />
          </div>
        </header>
      </div>
    );  
  }
}

export default App;
