//this primarily concerns UI, hence is stateless component, hence no need of class(viz. used for data i/o), only functional
import React from 'react';

const Home = ({users}) =>  {                      //passing props as a parameter to the function, props can be more than one separating by comma, here it is {users}
    const userList = users.map(user => {               //here we map through each individual user and store in the array userList
        return(
            <div className="user" key = {user.id}>
                <div>Name : {user.name} </div>
                <div>Age : {user.age} </div>
                <div>Belt : {user.belt} </div>
            </div>
        )
    })
    return(
        <div className="user-list">                 {/* here we output the whole list of users */}
            { userList }
        </div>
    )
}
export default Home                                 //Nested it to the Root component i.e. App.js
